function tagging_AllowEnhancedUI() {
    return false;
  }

function cdCheckSearchInput(form) {
    var query = '' + form.cdQuery.value;
    if ( (query == '') || (query.match(/^\s*$/)) ) {
      alert("You must use at least one search term to perform a search.");
      return false;
    }
    return true;
  }

function cdLLFailure(divErrorID, divLoadID) {
    jQuery(divErrorID).css('display', '');
    jQuery(divLoadID).remove();
  }
  function cdLLSuccess(response, divErrorID, divLoadID, divContentID) {
    if (response.hasError) {
      if (response.content.length > 0) {
        jQuery(divErrorID).html(response.content);
      }
      cdLLFailure(divErrorID, divLoadID);
    } else {
      jQuery(divContentID).html(response.content);
    }
  }
  function cdLLHandler(masterID, handlerURL, handlerArgs, activationRange, timeout) {
    var divContentID = masterID + 'Content';
    var divLoadID    = masterID + 'Loading';
    var divErrorID   = masterID + 'Error';

    if (window.cdLLoading[masterID])
      return;

    var winPositionY = jQuery(window).scrollTop() + jQuery(window).height();
    var elementFound = jQuery(divContentID).size();
    var divPositionY = jQuery(divContentID).offset().top;

    if (elementFound && ( divPositionY - winPositionY
< activationRange )) {
      window.cdLLoading[masterID] = true;
      jQuery.ajax({
        url      : handlerURL,
        cache    : false,
        timeout  : timeout,
        dataType : "json",
        data     : handlerArgs,
        error    : function() {cdLLFailure(divErrorID, divLoadID)},
        success  : function(response) {cdLLSuccess(response, divErrorID, divLoadID, divContentID)}
      });
    }
  }

